package com.xgd.boss.core.codec;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;

import org.springframework.util.Base64Utils;

public class Base64Image {
    public static void main(String[] args) {
        // 测试从Base64编码转换为图片文件
        String strImg = "";
        GenerateImage(strImg, "c:\\work\\wangyc.jpg");
        
        // 测试从图片文件转换为Base64编码
        System.out.println(getImageStr("c:\\work\\wangyc.jpg"));
    }

    public static String getImageStr(String imgFilePath) {// 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
        byte[] data = null;
        
        // 读取图片字节数组
        try {
            InputStream in = new FileInputStream(imgFilePath);
            data = new byte[in.available()];
            in.read(data);
            in.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        
        // 对字节数组Base64编码
        return Base64Utils.encodeToString(data);// 返回Base64编码过的字节数组字符串
    }

    public static boolean GenerateImage(String imgStr, String imgFilePath) {// 对字节数组字符串进行Base64解码并生成图片
        if (imgStr == null) // 图像数据为空
            return false;
        try {
            // Base64解码
            byte[] bytes = Base64Utils.decodeFromString(imgStr);
            for (int i = 0; i < bytes.length; ++i) {
                if (bytes[i] < 0) {// 调整异常数据
                    bytes[i] += 256;
                }
            }
            File img=new File(imgFilePath);
            if (img.exists()) {
				return false;
			}else {
				if (!img.createNewFile()) {
					return false;
				}
			}
            // 生成jpeg图片
            OutputStream out = new FileOutputStream(img);
            out.write(bytes);
            out.flush();
            out.close();
            return true;
        } catch (Exception e) {
            return false;
        }
    }
}